import { nameService } from '../../services/name.service.js';

export function loadNames(filterBy) {
    return async dispatch => {
        try {
            const names = await nameService.loadNames(filterBy);
            dispatch({ type: 'SET_NAMES', names });
        } catch (err) {
            console.log('Name Actions: err in loaded Names', err);
        }
    };
}

export function setName(name) {
    return async dispatch => {
        try {
            dispatch({ type: 'SET_NAME', name });
        } catch (err) {
            console.log('Name Actions: err in loaded Names', err);
        }
    };
}
