import axios from 'axios'

const BASE_URL = process.env.NODE_ENV === 'production' ? '/api/' : '//localhost:3030/api/'

export const httpService = {
    get(endpoint, data) {
        console.log('http get', endpoint);
        return ajax(endpoint, 'GET', data)
    },
    post(endpoint, data) {
        return ajax(endpoint, 'POST', data)
    },
    put(endpoint, data) {
        return ajax(endpoint, 'PUT', data)
    },
    delete(endpoint) {
        return ajax(endpoint, 'DELETE')
    }
}

async function ajax(endpoint, method = 'GET', data = null) {
    console.log('data', data);
    console.log('endpoint', endpoint);
    console.log('method', method);
    try {
        console.log(data);
        if (endpoint !== "order/") {
            const res = await axios({
                url: `${endpoint}`,
                method,
                data,
                //ידני זה עובד
                data: "TerminalNumber=1000&Operation=1&UserName=barak9611&SumToBill=109.50&CoinId=1&Language=he&ProductName=C101G&APILevel=10&Codepage=65001&SuccessRedirectUrl=http://www.site.com/Success.html&ErrorRedirectUrl=http://www.site.com/Error.html&IndicatorUrl=http://www.site.com/hide.aspx",
                params: (method === 'GET') ? data : null
            })
            console.log('res.data', res.data);
              resUrl(res.data)
        } else {
            const res = await axios({
                url: `${BASE_URL}${endpoint}`,
                method,
                data,
                params: (method === 'GET') ? data : null
            })
            console.log('res.data', res.data);
            console.log(res.data);
            console.log(JSON.stringify(res.data));
            return JSON.stringify(res.data)
            // return res.data
        }
    } catch (err) {
        console.log(`Had Issues ${method}ing to the backend, endpoint: ${endpoint}, with data: ${data}`)
        console.dir(err)
        if (err.response && err.response.status === 401) {
            // Depends on routing startegy - hash or history
            window.location.assign('/#/login')
            // window.location.assign('/login')
            //     router.push('/login')
            // }
            throw err
        }
    }
}

  function resUrl(data) {
    let urlStart = data.indexOf("&url=")
    // let urlEnd = data.indexOf("&", urlStart+1)
    let urlEnd = data.indexOf("&PayPalUrl=")
    console.log('start', urlStart);
    console.log('end', urlEnd);

    let fullUrl = data.substr( urlStart+5 )
     console.log('fullUrl', fullUrl);
}
