const initialState = {
    names: [],
    selectedName: {},
};

export function nameReducer(state = initialState, action = {}) {
    switch (action.type) {
        case 'SET_NAMES':
            return {
                ...state, names: action.names
            };
        case 'SET_NAME':
            return {
                ...state, selectedName: action.name
            };
            case 'SELECTED_NAME':
                return { ...state, selectedName: action.name }
    }
}
