    console.log('action mediaLink', mediaLink);
    return async dispatch => {
        try {
            dispatch({
                type: 'SET_MEDIA_LINK',
                mediaLink
            });
        } catch (err) {
            console.log('Name Actions: err in loaded Names', err);
        }
    };
}
