import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Button, TextField } from "@material-ui/core";

import { setOrderDetails } from "../store/action.js";

export function OrderForm() {
  const dispatch = useDispatch();

  const initialUserOrder = {
    fullName: "",
    phoneNumber: 0,
    address: {
      city: "",
      street: "",
      number: 0,
    },
  };
  const [order, setOrder] = useState(initialUserOrder);

  useEffect(() => {
    console.log("order", order);
    dispatch(setOrderDetails(order));
  }, [order]);

  const handleChange = ({ target }) => {
    const field = target.name;
    const value = target.value;
    if (
      field === address.city ||
      field === address.street ||
      field === address.number
    ) {
      setOrder({ ...order, ...address, [field]: value });
    }
    setOrder({ ...order, [field]: value });
  };

  return (
    <form>
      <TextField
        label="שם מלא"
        variant="outlined"
        name="fullName"
        // value={fullName}
        onChange={handleChange}
        required
      />
      <TextField
        label="טלפון"
        variant="outlined"
        name="phoneNumber"
        // value={phoneNumber}
        onChange={handleChange}
        required
      />
      <h4>כתובת למשלוח</h4>
      <TextField
        label="עיר"
        variant="outlined"
        name="address.city"
        // value={address.city}
        onChange={handleChange}
        required
      />
      <TextField
        label="רחוב"
        variant="outlined"
        name="address.street"
        // value={address.street}
        onChange={handleChange}
        required
      />
      <TextField
        label="מס בית"
        variant="outlined"
        name="address.number"
        // value={address.street}
        onChange={handleChange}
        required
      />
      {/* Î<Button variant='outlined' type='submit' disabled={!isValidInput}> */}
      Î
      <Button variant="outlined" type="submit">
        submit
      </Button>
    </form>
  );
}
