import React, { useEffect } from "react";

import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";

import { useSelector, useDispatch } from "react-redux";
import { setMediaType, setMediaLink } from "../store/action.js";


const mediaTypes = [
  {
    value: "instegram",
    label: "לינק לאינסטגרם",
  },
  {
    value: "facebook",
    label: "לינק לפייסבוק",
  },
  {
    value: "tiktok",
    label: "לינק לטיקטוק",
  },
  {
    value: "twitter",
    label: "לינק לטוויטר",
  },
];

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "32ch",
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: "#9469dd",
      },
      "&:hover fieldset": {
        borderColor: "#9469dd",
      },
      "&.Mui-focused fieldset": {
        borderColor: "#9469dd",
      },
    },
  },
}));

export function MediaDetails() {
  const dispatch = useDispatch();
  const classes = useStyles();

  const [type, setType] = React.useState("instegram");
  const [link, setLink] = React.useState("some link");

  useEffect(() => {
    dispatch(setMediaType(type));
    dispatch(setMediaLink(link));
    // eslint-disable-next-line
  }, [type, link]);

  const handleChangeSelect = ({ target }) => {
    console.log("target.value", target.value);
    setType(target.value);
  };
  const handleChangeLink = ({ target }) => {
    console.log("target.value", target.value);
    setLink(target.value);
  };

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <div className="select">
        <TextField
          id="outlined-select-currency"
          select
          label=""
          value={type}
          onChange={handleChangeSelect}
          helperText=""
          variant="outlined"
        >
          {mediaTypes.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      </div>
      <div className="txt-link">
        <TextField
          // label="הזינו כאן לינק לפרופיל" {type.label} "האינסטגרם שלכם"
        //   label= {mediaTypes.filter((option) => (
        //     option.value === {type}
        //   )
        //   option.label
        //   )
        // }
        label = {
          mediaTypes.filter((option)=>{
            option.value === {type}
          })
        }
          variant="outlined"
          name="userLink"
          // value={email}
          onChange={handleChangeLink}
          required
        />
      </div>
    </form>
  );
}
