const initialState = {
    mediaType: 'facebook',
    mediaLink: 'www',
};

export function mediaReducer(state = initialState, action = {}) {
    switch (action.type) {
        // case 'SET_NAMES':
        //     return {
        //         ...state, names: action.names
        //     };
        // case 'SET_NAME':
        //     return {
        //         ...state, selectedName: action.name
        //     };
        case 'SET_MEDIA_LINK':
            console.log('reducer',action.mediaLink);
                return { ...state, mediaLink: action.mediaLink
            };
        case 'SELECTED_MEDIA_TYPE':
            console.log('reducer',action.mediaName);
                return { ...state, mediaType: action.mediaName
            };
                default:
                    return state
    }
}
