import React, { useState, Fragment, useEffect } from "react";
import { Link } from "react-router-dom";
import TapTouchImg from "../assets/img/TapTouch.jpg";
import { useSelector, useDispatch } from "react-redux";
import { setMediaType, setMediaLink } from "../store/action.js";

import { Select } from "../cmps/Select.jsx";

import Button from "@material-ui/core/Button";
// import Icon from '@material-ui/core/Icon';
import { makeStyles } from '@material-ui/core/styles';
import TextField from "@material-ui/core/TextField";
import { red } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "32ch",
    },
    '& label.Mui-focused': {
      color: 'green',
    },
  },
}));

export function Home() {
  const classes = useStyles();

  const dispatch = useDispatch();
  const { mediaType } = useSelector((state) => state.mediaModule);

  const initialUserMedia = {
    type: mediaType,
    mediaLink: "https://www.instagram.com/danasandman/",
  };

  const [userMedia, setUserMedia] = useState(initialUserMedia);

  useEffect(() => {
    console.log("user madia changed", userMedia.mediaLink);
    dispatch(setMediaLink(userMedia.mediaLink));
    // eslint-disable-next-line
  }, [userMedia.mediaLink]);

  const handleChange = ({ target }) => {
    const value = target.value;
    setUserMedia((prevUserMedia) => ({ ...prevUserMedia, mediaLink: value }));
    console.log("userMedia", userMedia);
    console.log("userMedia.mediaLink", userMedia.mediaLink);
  };

  return (
    <Fragment>
      <title> Home page</title>
      <h2 className="main-title">טאץ׳ והחלפתם פרטים</h2>
      <img src={TapTouchImg} className="main-img" alt="main-img" />
      <h4>?אז מה תרצו שהטאפ טאץ׳ שלכם יעשה</h4>
      <Select />
      <form className={classes.root} noValidate autoComplete="off">
      <div>
      <TextField
        label="הזינו כאן לינק לפרופיל האינסטגרם שלכם"
        variant="outlined"
        name="userLink"
        // value={email}
        onChange={handleChange}
        required
      />
      </div>
      </form>
      <h4> !עלות התג 49 ש״ח בלבד כולל משלוח</h4>
      <Button variant="contained" color="default" disableElevation>
        <Link to="/order"> 〱  הזמינו עכשיו</Link>
      </Button>
    </Fragment>
  );
}
