import {
    orderService
} from '../services/order.service.js';

export function setMediaLink(mediaLink) {
    console.log('action mediaLink', mediaLink);
    return async dispatch => {
        try {
            dispatch({
                type: 'SET_MEDIA_LINK',
                mediaLink
            });
        } catch (err) {
            console.log('Name Actions: err in loaded Names', err);
        }
    };
}
export function setMediaType(mediaName) {
    console.log('action mediaName', mediaName);
    return async dispatch => {
