import React, { useState, Fragment, useEffect, link} from "react";
import { useSelector, useDispatch } from "react-redux";

import { setMediaType, setMediaLink } from "../store/action.js";

//UI UTIL
import { MultilineTextFields } from "../cmps/Select.jsx";
import TextField from "@material-ui/core/TextField";

export function Home() {

  const dispatch = useDispatch();
  const { mediaType } = useSelector((state) => state.mediaModule);

  const initialUserMedia = {
    type: mediaType,
    mediaLink: "https://www.instagram.com/danasandman/",
  };

  const [userMedia, setUserMedia] = useState(initialUserMedia);

  useEffect(() => {
    console.log("user madia changed", userMedia.mediaLink);
    dispatch(setMediaLink(userMedia.mediaLink));
    // eslint-disable-next-line
  }, [userMedia.mediaLink]);

  const handleChange = ({ target }) => {
    const value = target.value;
    //   setUserMedia({...userMedia, mediaLink: value });
    setUserMedia((prevUserMedia) => ({ ...prevUserMedia, mediaLink: value }));
    console.log("userMedia", userMedia);
    console.log("userMedia.mediaLink", userMedia.mediaLink);
  };

  return (
    <Fragment>
      <title> Home page</title>
      <h2>?אז מה תרצו שהטאפ טאץ׳ שלכם יעשה</h2>
      <MultilineTextFields />
      <TextField
        label="הזינו כאן לינק לפרופיל האינסטגרם שלכם"
        variant="outlined"
        name="userLink"
        // value={email}
        onChange={handleChange}
        required
      />
      <h2> !עלות התג 49 ש״ח בלבד כולל משלוח</h2>
      {/* onClick="" */}
      <button><link to="/order"> - הזמינו עכשיו</link></button>

      <span>{userMedia.type}</span>
    </Fragment>
  );
}
