import React, { useState, Fragment } from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

import { OrderForm } from "../cmps/OrderForm.jsx";

import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
    width: "20ch",
  },
}));

export function Order() {
  const classes = useStyles();
  const { mediaType, mediaLink, orderDetails } = useSelector(
    (state) => state.mediaModule
  );

  const initialUserDetails = {
    type: mediaType,
    link: mediaLink,
    order: orderDetails,
  };
  const [userDetails, setUserDetails] = useState(initialUserDetails);

  return (
    <Fragment>
      <h2>Order page</h2>
      <div>
        <h3>פרטי ההזמנה</h3>
        <h4>{userDetails.type} :סוג</h4>
        <h4>{userDetails.link} :לינק</h4>
      </div>
      <OrderForm></OrderForm>
      <Button
        variant="contained"
        className={classes.button}
        color="default"
        disableElevation
      >
        <Link to="/completeorder"> 〱 רכוש עכשיו</Link>
      </Button>
    </Fragment>
  );
}
