const initialState = {
    names: [],
    selectedName: null,
};

export function artReducer(state = initialState, action = {}) {
    switch (action.type) {
        case 'SET_NAMES':
            return {
                ...state, names: action.names
            };
        case 'SET_NAME':
            return {
                ...state, selectedName: action.name
            };
    }
}
